css:
/* Cor Degrade Menu */
main .header {
    background: linear-gradient(85.36deg, rgb(98 59 251) 3.91%, rgb(46 146 247) 98.18%);
    border-bottom: none;
}
/* Cor Degrade Menu */


/* Cor login e carrinho *Branco */
main #user-button i:before {
    background-color: #ffffff;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}

main #cart-button i:before {
    background-color: #ffffff;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}
/* Cor login e carrinho *Branco */



/* Cor de fundo do Menu */
.header__submenu {
    background: #000000;
    border-top: 1px solid #000000;
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(30%,1fr));
    margin-top: var(--spacer-4);
    max-width: 500px;
    min-width: 300px;
    overflow: auto;
    padding: var(--spacer-3);
    position: absolute;
    text-align: left;
}
/* Cor de fundo do Menu */


/* Cor da font do Menu */
main .header__nav a {
    color: #ffffff;
}
/* Cor da font do Menu */


/* Bordas arredondas Buscas */
.custom-select, .ec-identify-email, .form-control, .header__search, .menu__item {
    border-radius: 6px;
}
/* Bordas arredondas Buscas */

/* Texto INFO */

element.style {
    /* background: #ff0000; */
    /* color: #328d5b; */
    background: var(--light);
    width: 100%;
    content: url(/img/uploads/banner-desconto-topo-teste.gif);
}
/* Texto INFO */

/* Quantidade de Produtos na Listagem */
@media screen and (min-width: 998px) {
    .product-item {
        max-width: 225px;
    }

    .glide__slide.products-carousel__item {
        width: 222px !important;
    }
}
/* Quantidade de Produtos na Listagem */


/* Linha Titulo Produto Listagem */
.products-carousel__title:before {
    background: #dee2e600;
    content: " ";
    height: var(--border-width);
    left: 10%;
    margin-top: -0.05rem;
    position: absolute;
    top: 50%;
    width: 80%;
}
/* Linha Titulo Produto Listagem a Esquerda */
.products-carousel__title {
    display: block;
    margin: 0;
    padding: var(--spacer-3);
    position: relative;
    text-align: left;
}

/* Linha Titulo Produto Listagem */

/* Botão Comprar Listagem Verde e Hover Verde Claro */
.btn-primary {
    background-color: #25BA34;
    border-color: #25BA34;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
.btn-primary:focus, .btn-primary:hover {

    background-color: #0aab45;
    border-color: #018937;
    border-radius: 0.375rem;
    text-transform: uppercase;
}
/* Botão Comprar Listagem Verde e Hover Verde Claro */


/* Icone carrinho Detalhes */
.fa-shopping-bag:before, .i-shopping-bag:before {
    content: "";
    background-color: #ffffff;
    content: "";
    display: inline-block;
    height: 20px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    width: 22px;
}
/* Icone carrinho Detalhes */
