css:
main .header {
    background-color: #3b1d58;
    border-bottom: none;
}

main #user-button i:before {
    background-color: #ffffff;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/login.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}


main #cart-button i:before {
    background-color: #ffffff;
    content: "";
    display: inline-block;
    height: 36px;
    -webkit-mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    mask: url(https://ecom.nyc3.digitaloceanspaces.com/storefront/clean/cart.svg) no-repeat 50% 50%;
    -webkit-mask-size: cover;
    mask-size: cover;
    width: 36px;
}

i[class*=" fa-"], i[class*=" i-"], i[class^=fa-], i[class^=i-] {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    display: inline-block;
    font-family: Storefront Icons;
    font-style: normal;
    font-variant: normal;
    font-weight: 400;
    line-height: 1em;
    text-decoration: inherit;
    text-transform: none;
    color: black;
}

.header__submenu {
    background: #000000;
    border-top: 1px solid #000000;
    box-shadow: var(--box-shadow-sm);
    display: none;
    gap: var(--spacer-2);
    grid-template-columns: repeat(auto-fit,minmax(30%,1fr));
    margin-top: var(--spacer-4);
    max-width: 500px;
    min-width: 300px;
    overflow: auto;
    padding: var(--spacer-3);
    position: absolute;
    text-align: left;
}


main .header__nav a {
    color: #ffffff;
}
